import React from "react";
import Button from "../button/button";
import InputField from "../input/inputField";
import SelectField from "../selectField/selectField";
import TextAreaField from "../textArea/textAreaField";
import Avatar from "../../img/icons8-checked-user-male-80.png";


class Form extends React.Component {
    constructor( props ) {
        super( props );
        this.state = {};
        this.contact = props.contact;
        this.onChange = props.onChange;
    }

    afficher = ( event ) => {
        event.preventDefault();
        // this.listContact.push( this.contact );
        // this.props.listContact.push( this.contact );
        // console.log( this.listContact )
    }

    render () {
        return (
            <form onSubmit={this.afficher}>
                <InputField type="text" id="prenom" name="prenom" label="Prenom" onChange={this.props.handleChange} value={props.contact.prenom} />
                <InputField type="text" id="nom" name="nom" label="nom" onChange={this.props.handleChange} />
                <SelectField id="groupe" name="group" label="Groupe" onChange={this.props.handleChange} />
                <TextAreaField htmlFor="biographie" label="Biographie" id="biographie" name="biographie" cols="30" rows="3" onChange={this.props.handleChange} />
                <div className="div-select-img">
                    <div>
                        <input type="file" name="hoix-photo" id="choix-photo" accept=".jpg,.png" />
                    </div>
                    <div className="div-photo">
                        <img src={Avatar} alt="" width="100" height="100" />
                    </div>
                </div>
                <div className="div-btn">
                    <Button type="submit" label="Créer" className="btn" id="btn-creer-contact" />
                    <Button type="reset" label="Réinit" className="btn" id="btn-reinit" />
                </div>
            </form>
        );
    }
};
export default Form;
