import React from "react";
import Button from "../button/button";
import InputField from "../input/inputField";
// import SelectField from "../selectField/selectField";
import TextAreaField from "../textArea/textAreaField";
import Avatar from ".";


class Form extends React.Component {
    constructor( props ) {
        super( props );
        this.state = {};
        this.data = {};
    }
    afficher () {
        alert( 'clic' );
    }

    render () {
        return (
            <form>
                <InputField type="text" id="prenom" label="Prenom" />
                <InputField type="text" id="nom" label="nom" />
                {/* <SelectField id="groupe" label="Groupe" /> */}
                <TextAreaField htmlFor="bio" label="Biographie" id="bio" cols="30" rows="3" />
                <div className="div-select-img">
                    <div>
                        <input type="file" name="choisir un fichier" id="choix-photo" accept=".jpg,.png" />
                    </div>
                    <div class="div-photo">
                        <img src="/" alt="" width="100" height="100" />
                    </div>
                </div>
                <div className="div-btn">
                    <Button onClick={this.afficher} type="submit" label="Créer" className="btn" id="btn-creer-contact" />
                    <Button type="reset" label="Réinit" className="btn" id="btn-reinit" />
                </div>
            </form>
        );
    }
};
export default Form;
